import React, { Component }  from 'react';
import './App.css';
import Stopwatch  from './component/watch';
import Eventhandler from './component/eventhandler';
import Counter from './component/counter';
import Ref from './component/ref';
import Bform from './component/bform';
import Nameform from './component/dform';
import Fancyform from './component/fancyform';
import Listitem from './component/List';
state = {username: undefined}
handleSubmit = e => {
  e.preventDefault()
  this.setState({
    username: this.inputNode.value,
  })
}
function App() {

  const {username} = this.state
  return (
    <div className="App">
      <Eventhandler/>
      <Stopwatch/>
      <Counter/>
      <Ref/>
      <Bform/>
      <Nameform
       getErrorMessage={value => {
        if (value.length < 3) {
          return `Value must be at least 3 characters, but is only ${value.length}`
        }
        if (!value.includes('s')) {
          return `Value does not include "s" but it should!`
        }
        return null
      }}
      />
      <Fancyform/>
      <Listitem/>
    </div>
  );
}

export default App;
