import React from "react";

class Listitem extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            items: "",
        }
    }
    render(){
        const {items} = this.state
        return(
            <form>
                <h1>List of Items</h1>
                <div className="form-group">
                <button>+ Add Items</button>
                {items.map((i, index) => (
                <div>
                  <button
                    onClick={() => this.removeItem(i)}
                  >
                    -
                  </button>
                  {i.value}:
                  <input />
                </div>
              ))}
                </div>

            </form>
        )
    }
}
export default Listitem;
