import React from "react";

class Fancyform extends React.Component{

      constructor(props) {
        super(props)
        this.state = {
            commaSeparated: '',
        }
    }
    static availableOptions = [
        'apple',
        'grape',
        'cherry',
        'orange',
        'pear',
        'peach',
      ]
      handalCommaseparated = (e) => {
        const {value} = e.target
        const allVals = value
        .split(',')
        .map(v => v.trim())
        .filter(Boolean)
       this.setState ({
        commaSeparated: e.target.value,
        multiline: allVals.join('\n'),
        multiSelect: allVals.filter(v =>
            Fancyform.availableOptions.includes(v),
          ),
        })
      }
      multilinevalue = (e) => {
        const {value} = e.target
        const allVals = value
          .split('\n')
          .map(v => v.trim())
          .filter(Boolean)
      }
    render(){
        const {commaseparated} = this.state
        return(
            <form>
                <h1>Fancy Form</h1>
                <div className="form-group">
                    <label>comma separated values</label>
                    <input type="text" onChange={this.handalCommaseparated} value={commaseparated} placeholder="Enter comma separated values"/>
                </div>
                <div className="form-group">
                    <label>Multiline Value</label>
                    <textarea onChange={this.multilinevalue} placeholder="Enter Multiline Value">

                    </textarea>
                </div>
                <div className="form-group">
                 <label>Multiselect Value</label>
                 <select 
                 multiple
                 size={
                    Fancyform.availableOptions
                      .length
                  }
                 >
                 {Fancyform.availableOptions.map(
                      optionValue => (
                        <option
                          key={optionValue}
                          value={optionValue}
                        >
                          {optionValue}
                        </option>
                      ),
                    )}
                 </select>
                </div>
            </form>
        )
    }
}
export default Fancyform;
