class Counter extends React.Component{
    constructor(props) {
        super(props)
        this.state = {
            count: 0,
        }
    }
    hendalClick = () =>{
        this.setState({count: count + 1})
    }
    render(){
        const {count } = this.state
         return(
            <>
            <button onClick={hendalClick}>{count}</button>
            </>
         )
    }
}
export default Counter;
