import React from "react";

class Nameform extends React.Component {
    handalsubmit = (e) =>{
        e.preventDefault()
        const value =
        e.target.elements.username.value
        const error = this.props.getErrorMessage(
            value,
          )
          if (error) {
            alert(`error: ${error}`)
          } else {
            alert(`success: ${value}`)
          }
    }
    handelChange = (e) =>{
      const value = e.target;
      this.setState({
        error: this.getErrorMessage(value)
      })

    }
    render(){
        const {error} = this.state
        return(
            <form onSubmit={this.handalsubmit}>
                <h1>Dynamic form</h1>
                <div className="form-group">
                    <label>Name</label>
                    <input type="text" name="username" onChange={this.handelChange} placeholder="Enter Name"/>
                </div>
                {error ? (
                <div style={{color: 'red'}}>
                  {error}
                </div>
              ) : null}
                <div className="form-group">
                    <button disabled={Boolean(error)} type="submit">submit</button>
                </div>
            </form>
        )
    }
}
export default Nameform;
