import React from "react";

class Bform extends React.Component(){
    render(){
        return(
            <>
            <h1>Basic Form</h1>
            <div className="from-group">
                <label>First Name</label>
                <input 
                type="text" 
                name="username"
                ref={input => (this.firstname = input)}/>
            </div>
            <input type="submit"/>
            </>
        )
    }
}
export default Bform;
