import React from "react";

class Fancyform extends React.Component{
    static availableOptions = [
        'apple',
        'grape',
        'cherry',
        'orange',
        'pear',
        'peach',
      ]
      handalCommaseparated = () => {

      }
      multilinevalue = () => {

      }
    render(){
        return(
            <form>
                <h1>Fancy Form</h1>
                <div className="form-group">
                    <label>comma separated values</label>
                    <input type="text" onChange={this.handalCommaseparated} placeholder="Enter comma separated values"/>
                </div>
                <div className="form-group">
                    <label>Multiline Value</label>
                    <textarea onChange={this.multilinevalue} placeholder="Enter Multiline Value">

                    </textarea>
                </div>
                <div className="form-group">
                 <label>Multiselect Value</label>
                 <select 
                 multiple

                 >
                 {Fancyform.availableOptions.map(
                      optionValue => (
                        <option
                          key={optionValue}
                          value={optionValue}
                        >
                          {optionValue}
                        </option>
                      ),
                    )}
                 </select>
                </div>
            </form>
        )
    }
}
export default Fancyform;
